script:
frappe.ui.form.on('Purchase Order', {
schedule_date:function(frm,cdt,cdn)
	{
	  var d= locals[cdt][cdn]
	  if(d.tax_category=='UAE VAT 5% Standard')
	  {
	       frm.set_value("taxes_and_charges", "UAE VAT 5% - NC");  
	  }
	  if(d.tax_category=='RCM – 5% VAT')
	  {
	       frm.set_value("taxes_and_charges", "RCM – 5% VAT - NC");
	       frm.set_value("reverse_charge", "Y");  
	  }
	},
	validate:function(frm) {
        $.each(frm.doc.items || [], function(i, v) {
       frappe.model.set_value(v.doctype, v.name,"project",frm.doc.project)
    });
	},
	validate:function(frm){
	   frappe.call({
                    method: 'frappe.client.get_value',
                    args: {
                        'doctype': 'Project',
                        'filters': {'name': frm.doc.project},
                        'fieldname': [
                            'project_manager'
                        ]
                    },
                    callback: function(s) {
                        if (!s.exc) {
                           frm.set_value("project_manager", s.message.project_manager);

                        }
                    }
	   })

	},
	cost_center:function(frm,cdt,cdn)
	{
	    var d=locals[cdt][cdn]
	    frm.set_value("department",d.cost_center ); 
	}
})
